import 'package:flutter/material.dart';import '../../cubit/app_cubit.dart';import 'defultebuttom.dart';import 'defultetinputnumbers.dart';class DataInformation extends StatelessWidget {  final TextEditingController widthController;  final TextEditingController heightController;  final TextEditingController ageController;  const DataInformation({    super.key,    required this.widthController,    required this.heightController,    required this.ageController,  });  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.symmetric(horizontal: 50, vertical: 10),      child: Column(        children: [          inputenumber(            minvalue: 2,            maxvalue: 100,            writting: (value) {              AppCubit.get(context).changNumberAge(value);            },            name: 'Age',            number: AppCubit.get(context).numberAge,          ),          inputenumber(            minvalue: 3,            maxvalue: 500,            writting: (value) {              AppCubit.get(context).changeNumberWidth(value);            },            name: 'Width',            number: AppCubit.get(context).numberWidth,          ),          inputenumber(            minvalue: 10,            maxvalue: 200,            writting: (value) {              AppCubit.get(context).changeNumberHeight(value);            },            name: 'Height',            number: AppCubit.get(context).numberHeight,          ),          defultebuttom(              press: () {                AppCubit.get(context).calculateBodyMassIndex(                  inputAge: AppCubit.get(context).numberAge,                  inputHeight: AppCubit.get(context).numberHeight,                  inputWidth: AppCubit.get(context).numberWidth,                );              },              gradient: const LinearGradient(colors: <Color>[                Color(0xFF8F6FDA),                Color(0xFF618DD8),              ]),              panding: const EdgeInsets.all(20),              border: BorderRadius.circular(50),              icon: Icons.calculate_rounded)        ],      ),    );  }}