import 'package:bmi/constant.dart';import 'package:flutter/material.dart';import '../../cubit/app_cubit.dart';import 'custom_button.dart';import 'custom_input_number.dart';class PartBody extends StatelessWidget {  final TextEditingController widthController;  final TextEditingController heightController;  final TextEditingController ageController;  const PartBody({    super.key,    required this.widthController,    required this.heightController,    required this.ageController,  });  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.symmetric(horizontal: 50, vertical: 10),      child: Column(        children: [          NumberInputWidget(            minvalue: 2,            maxvalue: 100,            valueChanged: (value) {              AppCubit.get(context).changNumberAge(value);            },            name: 'Age',            number: AppCubit.get(context).numberAge,          ),          NumberInputWidget(            minvalue: 3,            maxvalue: 500,            valueChanged: (value) {              AppCubit.get(context).changeNumberWidth(value);            },            name: 'Width',            number: AppCubit.get(context).numberWidth,          ),          NumberInputWidget(            minvalue: 10,            maxvalue: 200,            valueChanged: (value) {              AppCubit.get(context).changeNumberHeight(value);            },            name: 'Height',            number: AppCubit.get(context).numberHeight,          ),          customButton(              press: () {                AppCubit.get(context).calculateBodyMassIndex(                  inputAge: AppCubit.get(context).numberAge,                  inputHeight: AppCubit.get(context).numberHeight,                  inputWidth: AppCubit.get(context).numberWidth,                );              },              gradient: const LinearGradient(colors: <Color>[                Color(0xFF8F6FDA),                Color(0xFF618DD8),              ]),              padding: const EdgeInsets.all(20),              border: BorderRadius.circular(50),              icon: AppIcons.calculateButton)        ],      ),    );  }}