import 'package:bmi/constant.dart';import 'package:flutter/material.dart';import '../../cubit/app_cubit.dart';import 'custom_button.dart';class Six extends StatelessWidget {  const Six({super.key});  @override  Widget build(BuildContext context) {    return Row(      children: [        Expanded(            child: customButton(                icon: AppIcons.female,                border: const BorderRadius.only(                    bottomRight:                    Radius.circular(50),                    topRight: Radius.circular(50)),                color: const Color.fromRGBO(                    164, 97, 219, 1),                padding: const EdgeInsets.all(30),                press: () {                  AppCubit.get(context)                      .six(six: 'Female');                })),        Expanded(            child: customButton(                icon: AppIcons.male,                border: const BorderRadius.only(                    bottomLeft: Radius.circular(50),                    topLeft: Radius.circular(50)),                color: Colors.blueAccent,                padding: const EdgeInsets.all(30),                press: () {                  AppCubit.get(context)                      .six(six: 'Male');                })),      ],    );  }}